import ParkingMeter from "./components/ParkingMeter";
import Buttons from "./components/Buttons";
import Screen from "./components/Screen";
import { React, useState } from "react";
import coin_img from "./assets/coin.png";
import card_img from "./assets/card.png";

function App() {
  const [time, setTime] = useState(0);
  const [message, setMessage] = useState('$2.00/hr 8 hours Max');
  const [coinInserted, setCoinInserted] = useState(false);
  const [cardInserted, setCardInserted] = useState(false);
  const [walletMessage, setWalletMessage] = useState('');

  const setCoinPayment = () => {
    if (!cardInserted) {
      setCoinInserted(true);
      setTime(15);
    }
  }

  const addCoin = () => {
    if (time < 480) { // Time less than 8 hours
      setTime(prevTime => prevTime + 15);
    }
  }

  const setCardPayment = () => {
    if (!coinInserted) {
      setCardInserted(true);
    } else {
      setWalletMessage('Please cancel your current coin transaction to use your credit card.')
    }
  }

  const cardClicked = () => {
    setWalletMessage('Please complete yur transaction on the parking meter screen.')
  }

  return (
    <div>
      <div className="wallet-wrapper">
        Wallet
        <div className="payment-wrapper">
          <div>
            {!coinInserted ? (
              <button onClick={setCoinPayment}>
                <img className="coin-img" src={coin_img} alt="Coin" />
              </button>
            ) : (
              <button onClick={addCoin}>
                <img className="coin-img clicked" src={coin_img} alt="Coin" />
              </button>
            )}
          </div>
          <div>
            {!cardInserted ? (
              <button onClick={setCardPayment}>
                <img className="card-img" src={card_img} alt="Credit Card" />
              </button>
            ) : (
              <button onClick={cardClicked}>
                <img className="coin-img clicked" src={card_img} alt="Coin" />
              </button>
            )}
          </div>
        </div>
        {walletMessage}
      </div>
      <div className="screen-wrapper">
        <Screen time={time} message={message} />
      </div>
      <div>
        <Buttons time={time} setTime={setTime} setMessage={setMessage} coinInserted={coinInserted} setCoinInserted={setCoinInserted} cardInserted={cardInserted} setCardInserted={setCardInserted} />
      </div>
      <ParkingMeter />
    </div>
  );
}

export default App;
