import React, { useState, useEffect } from "react";

const Screen = ({ time }) => {
    const [expirationTime, setExpirationTime] = useState(new Date().getTime());

    const formatTime = (time) => {
        const hours = Math.floor(time / 60);
        const minutes = time % 60;

        return `${hours < 10 ? '0' : ''}${hours}:${minutes < 10 ? '0' : ''}${minutes}`;
    };

    const calculateExpiration = (minutes) => {
        const currentTime = new Date();
        const newExpirationTime = new Date(currentTime.getTime() + minutes * 60000);
        setExpirationTime(newExpirationTime);
    }

    useEffect(() => {
        calculateExpiration(time);
    }, [time]);

    return (
        <div className="screen">
            <div className="total-time">
                <p>Total time: <b>{formatTime(time)}</b></p>
                <p>Expires: {formatTime(expirationTime.getHours() * 60 + expirationTime.getMinutes())}</p>
            </div>
        </div>
    );
};

export default Screen;
