import React, { useState } from 'react';
import { DndProvider } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';
import ParkingMeter from "./components/ParkingMeter";
import Buttons from "./components/Buttons";
import Screen from "./components/Screen";
import coin_img from "./assets/coin.png";
import card_img from "./assets/card.png";
import { useDrag, useDrop } from 'react-dnd';

function App() {
  const [time, setTime] = useState(0);
  const [message, setMessage] = useState('$2.00/hr 8 hours Max');
  const [coinInserted, setCoinInserted] = useState(false);
  const [cardInserted, setCardInserted] = useState(false);
  const [walletMessage, setWalletMessage] = useState('');
  const [{ isDragging }, drag, dragPreview] = useDrag(() => ({
    type: 'CARD',
    collect: (monitor) => ({
      isDragging: monitor.isDragging()
    })
  }))
  const [{ canDrop, isOver }, drop] = useDrop(() => ({
    accept: 'CARD',
    collect: (monitor) => ({
      isOver: monitor.isOver(),
      canDrop: monitor.canDrop()
    })
  }))

  /** COIN STUFF
  const setCoinPayment = (event) => {
    if (!cardInserted) {
      setCoinInserted(true);
      setTime(15);
      setWalletMessage('Click the coin again to add more coins to the machine.');

      // plus sign animation
      const plusSign = document.createElement('div');
      plusSign.classList.add('plus-sign');
      plusSign.style.left = `${event.clientX}px`;
      plusSign.style.top = `${event.clientY}px`;
      document.body.appendChild(plusSign);

      setTimeout(() => {
        plusSign.style.animation = 'floatUp 1s ease forwards';
      }, 10);

      setTimeout(() => {
        plusSign.remove();
      }, 1000);
    } else {
      setWalletMessage('Please cancel your current card transaction to use coins.')
    }
  }

  const addCoin = (event) => {
    if (time < 480) { // Time less than 8 hours
      setTime(prevTime => prevTime + 15);

      const plusSign = document.createElement('div');
      plusSign.classList.add('plus-sign');
      plusSign.style.left = `${event.clientX}px`;
      plusSign.style.top = `${event.clientY}px`;
      document.body.appendChild(plusSign);

      setTimeout(() => {
        plusSign.style.animation = 'floatUp 1s ease forwards';
      }, 10);

      setTimeout(() => {
        plusSign.remove();
      }, 1000);
    } else {
      setWalletMessage('You have reached the maximum time.')
    }
  }
  */

  const setCardPayment = () => {
    if (!coinInserted) {
      setCardInserted(true);
      setTime(0);
      setWalletMessage('Please complete the rest of your transaction on the parking meter screen.')
    } else {
      setWalletMessage('Please cancel your current coin transaction to use your credit card.')
    }
  }

  const cardClicked = () => {
    setWalletMessage('Please complete your transaction on the parking meter screen.')
  }

  const clearWalletMessage = () => {
    setWalletMessage('');
  }

  return (
    <DndProvider backend={HTML5Backend}>
      <div>
        <div ref={drop} className="drag-area"></div>
        <div className="wallet-wrapper">
          <u>WALLET</u>(Please drag and drop credit card into slot to begin)
          <div className="payment-wrapper">
            {/* COIN STUFF
            <div>
              {!coinInserted ? (
                <button onClick={setCoinPayment}>
                  <img className="coin-img" src={coin_img} alt="Coin" />
                </button>
              ) : (
                <button onClick={addCoin}>
                  <img className="coin-img coin-clicked" src={coin_img} alt="Coin" />
                </button>
              )}
            </div>
            */}
            <div>
              {!cardInserted ? (
                <button onClick={setCardPayment}>
                  <img ref={drag} className="card-img" src={card_img} alt="Credit Card" />
                </button>
              ) : (
                <button onClick={cardClicked}>
                  <img className="card-img card-clicked" src={card_img} alt="Credit Card" />
                </button>
              )}
            </div>
          </div>
          <div className="wallet-message">
            {walletMessage}
            {(walletMessage !== '') && 
              <button onClick={clearWalletMessage}>
                Okay
              </button>
            }
          </div>
        </div>
        <div className="screen-wrapper">
          <Screen time={time} message={message} />
        </div>
        <div>
          <Buttons time={time} setTime={setTime} setMessage={setMessage} setWalletMessage={setWalletMessage} coinInserted={coinInserted} setCoinInserted={setCoinInserted} cardInserted={cardInserted} setCardInserted={setCardInserted} />
        </div>
        <ParkingMeter cardInserted={cardInserted} />
      </div>
    </DndProvider>
  );
}

export default App;
